       }\n      }\n      l=2**.5*5*sp*1.01\n      for(i=4;i--;){\n        x.beginPath()\n        x.lineWidth=Math.min(500, 600/Z)\n        x.strokeStyle=i%2?\'#80f8\':\'#0f88\'\n        X=S(p=Math.PI*2/4*i+Math.PI/4)*l\n        Y=C(p)*l\n        Z=0\n        R(Rl,Pt,Yw,1)\n        x.lineTo(...Q())\n        X=S(p=Math.PI*2/4*(i+1)+Math.PI/4)*l\n        Y=C(p)*l\n        Z=0\n        R(Rl,Pt,Yw,1)\n        x.lineTo(...Q())\n        x.stroke()\n        x.lineWidth/=3\n        x.strokeStyle=i%2?\'#80f\':\'#0f8\'\n        x.stroke()\n      }\n      switch(stage){\n        case 0:\n          l_=[]\n          x.font=\'90px courier\'\n          x.textAlign=\'center\'\n          x.fillStyle=\'#fff\'\n          x.fillText(\'choose a color to go first\',960,70)\n          x.beginPath()\n          X=-16, Y=0, Z=0\n          R(Rl,Pt,Yw,1)\n          s=Math.min(500, 2e3/Z)\n          x.arc(...(l_[0]=Q()),s,0,7)\n          x.fillStyle=\'#80f\'\n          x.fill()\n          x.strokeStyle=\'#fff8\'\n          ga=t*200%200\n          x.beginPath()\n          x.arc(...Q(),s+ga,0,7)\n          x.globalAlpha=1/(1+ga**3/99999)\n          x.lineWidth=Math.min(500,500/Z)\n          x.stroke()\n          X=16, Y=0, Z=0\n          R(Rl,Pt,Yw,1)\n          x.beginPath()\n          x.arc(...Q(),s+ga,0,7)\n          x.globalAlpha=1/(1+ga**3/99999)\n          x.lineWidth=Math.min(500,500/Z)\n          x.stroke()\n          x.globalAlpha=1\n          x.beginPath()\n          s=Math.min(500, 2e3/Z)\n          x.arc(...(l_[1]=Q()),s,0,7)\n          x.fillStyle=\'#0f8\'\n          x.fill()\n          \n          for(let m=2;m--;){\n            v=l_[m]\n            d=Math.hypot(v[0]-mx,v[1]-my)\n            if(d<200){\n              begCol=m\n              x.beginPath()\n              x.strokeStyle=\'#0f8\'\n              s=120\n              x.arc(...v,s,0,7)\n              x.lineWidth=1e3/Z\n              x.stroke()\n            }\n          }\n        break\n        case 1:\n          tgt_=-1\n  