(600000, 'https://jsbot.cantelope.org/uploads/1tgfHk.mp4', 'song = \'https://cantelope.org/reflecting%20pool.mp3\'\nvid = \'https://cantelope.org/base%20videos/awakening%20base.mp4\'\nrecordingStarted=playing=vidplaying=analyzerSetup=0\n\nfftSize = 64\n\nc=document.querySelector(\'#c\')\nx=c.getContext(\'2d\')\nc.width=c.clientWidth\nc.height=c.clientHeight\ncanvas2=c.cloneNode()\nx2=canvas2.getContext(\'2d\')\n\nS=Math.sin\nC=Math.cos\nt=0\n\nDraw=()=>{\n\n  if(!t){\n    R=(Rl,Pt,Yw,o)=>{\n      X=S(p=(A=(M=Math).atan2)(X,Y)+Rl)*(d=(H=M.hypot)(X,Y)),Y=C(p)*d,Y=S(p=A(Y,Z)+Pt)*(d=H(Y,Z)),Z=C(p)*d,X=S(p=A(X,Z)+Yw)*(d=H(X,Z)),Z=C(p)*d\n      if(o)X+=oX,Y+=oY,Z+=oZ\n    }\n    Q=q=>[c.width/2+X/Z*800,c.height/2+Y/Z*800]\n    Rn=Math.random\n  }\n\n  if(vidplaying){\n    x.globalAlpha=.5\n    x.drawImage(bgvid,0,0,w=c.width,1080)\n    x.globalAlpha=1\n    x.fillStyle=\'#0003\'\n    x.fillRect(0,0,w=c.width,w)\n  }else{\n    x.fillStyle=\'#0004\'\n    x.fillRect(0,0,w=c.width,w)\n  }\n\n  Rl=t/6,Pt=S(t/2)/2,Yw=S(t/1.5)/1.5\n  oX=0,oY=0,oZ=4+S(t/1.5)/1.75\n  mt=S(t/4)\n  \n  if(!t){\n    I=(A,B,M,D,E,F,G,H)=>(K=((G-E)*(B-F)-(H-F)*(A-E))/(J=(H-F)*(M-A)-(G-E)*(D-B)))>=0&&K<=1&&(L=((M-A)*(B-F)-(D-B)*(A-E))/J)>=0&&L<=1?[A+K*(M-A),B+K*(D-B)]:0\n    x.lineJoin=x.lineCap=\'round\'\n    sd=6\n    iBr=2,iPv=.006,iPsv=.025,iPc=8,iPs=150,iRm=iPv*65,iRsm=iPsv*2.75,iPbv=.02\n    B=Array(sd).fill().map((v,i)=>{\n      return [S(p=Math.PI*2/sd*i)*iBr,C(p)*iBr,0]\n    })\n    P=Array(iPc).fill().map(v=>{return {X:0,Y:0,Z:0,vx:S(p=Rn()*Math.PI*2)*iPv,vy:C(p)*iPv,vz:0,theta:0,P:[]}})\n  }\n\n  P.map((v,j)=>{\n    x3_=v.X+=v.vx\n    y3_=v.Y+=v.vy\n    z3_=v.Z+=v.vz\n    d=Math.hypot(v.vx,v.vy,v.vz)\n    vxd=v.vx/d*iRm\n    vyd=v.vy/d*iRm\n    vzd=v.vz/d*iRm\n    x4_=x3_+vxd\n    y4_=y3_+vyd\n    z4_=z3_+vzd\n    B.map((q,i)=>{\n      if(i){\n        X=B[i-1][0]\n        Y=B[i-1][1]\n        Z=0\n      }else{\n        X=B[l=B.length-1][0]\n        Y=B[l][1]\n        Z=0\n      }\n      R(mt,0,0,0)\n      x2_=X,y2_=Y\n      X=q[0],Y=q[1],Z=0\n      R(mt,0,0,0)\n      x1_=X,y1_=Y\n      if(a=I(x1_,y1_,x2_,y2_,x3_,y3_,x4_,y4_)){\n        u=((x3_-x1_)*(x2_-x1_)+(y3_-y1_)*(y2_-y1_))/Math.hypot(x2_-x1_,y2_-y1_)\n        mx=u*(x2_-x1_)\n        my=u*(y2_-y1_)\n        v.vx+=S(p=Math.atan2(mx-v.X,my-v.Y)-Math.PI/2)/80\n        v.vy+=C(p)/80\n      }\n    })\n    d=Math.hypot(v.vx,v.vy)\n    v.vx/=d\n    v.vy/=d\n    v.vx*=iPv\n    v.vy*=iPv\n    for(m=2;m--;)v.P.push([v.X,v.Y,v.Z,S(p=v.theta+=Math.PI/sd*(1+j==3?5:1+j)+S(t/1.5)/16)*iPsv,C(p)*iPsv,0,iPs])\n    v.P.map(v=>{\n\n      x3_=v[0]\n      y3_=v[1]\n      z3_=v[2]\n      d=Math.hypot(v[3],v[4],v[5])\n      vxd=v[3]/d*iRsm\n      vyd=v[4]/d*iRsm\n      vzd=v[5]/d*iRsm\n      x4_=x3_+vxd\n      y4_=y3_+vyd\n      z4_=z3_+vzd\n      bnc=0\n      B.map((q,i)=>{\n        if(i){\n          X=B[i-1][0]\n          Y=B[i-1][1]\n          Z=0\n        }else{\n          X=B[l=B.length-1][0]\n          Y=B[l][1]\n          Z=0\n        }\n        R(mt,0,0,0)\n        x2_=X,y2_=Y\n        X=q[0],Y=q[1],Z=0\n        R(mt,0,0,0)\n        x1_=X,y1_=Y\n        if(!bnc&&(a=I(x1_,y1_,x2_,y2_,x3_,y3_,x4_,y4_))){\n          bnc=0\n          u=((x3_-x1_)*(x2_-x1_)+(y3_-y1_)*(y2_-y1_))/Math.hypot(x2_-x1_,y2_-y1_)\n          mx=u*(x2_-x1_)\n          my=u*(y2_-y1_)\n          v[3]+=S(p=Math.atan2(mx-v[0],my-v[1])-.8)/80*(d=.25+Math.hypot(v[3],v[4])*100)\n          v[4]+=C(p)/100*d\n        }\n      })\n      d=Math.hypot(v[3],v[4])\n      v[3]/=d\n      v[4]/=d\n      v[3]*=iPsv\n      v[4]*=iPsv\n\n      X=v[0]+=v[3]\n      Y=v[1]+=v[4]\n      Z=v[2]+=v[5]\n      R(Rl,Pt,Yw,1)\n      if(Z>0){\n        x.beginPath()\n        x.arc(...Q(),Math.min(99,(v[6]-=1.5)/Z/Z),0,7)\n        x.fillStyle=\'#1f84\'\n        x.fill()\n      }\n    })\n    v.P=v.P.filter(v=>v[6]>10)\n  })\n\n  B.map((v,i)=>{\n    x.beginPath()\n    if(i){\n      X=B[i-1][0]\n      Y=B[i-1][1]\n      Z=B[i-1][2]\n    }else{\n      X=B[l=B.length-1][0]\n      Y=B