(600063, 'https://jsbot.cantelope.org/uploads/1aGixh.mp4', 'song = \'https://cantelope.org/when the moon was made.mp3\'\nvid = \'\'\n\nfunction initVars() {\n\n  pi = Math.PI;\n  canvas = c//document.querySelector(\"#c\");\n  cx = canvas.width / 2;\n  cy = canvas.height / 2;\n  frames = seed = 0;\n  res = 20;\n  points = 10;\n  cols = parseInt(1920 / res);\n  rows = parseInt(1080 / res);\n}\n\nfunction rand() {\n  seed += .1234;\n  return parseFloat(\'0.\' + Math.sin(seed).toString().substr(6));\n}\n\nfunction loadControlPoints() {\n\n  controls = new Array(points);\n  for (i = 0; i < points; ++i) {\n    controls[i] = new Object();\n    controls[i].x = cols / 2;\n    controls[i].y = rows / 2;\n    controls[i].vx = 3 - rand() * 6;\n    controls[i].vy = 3 - rand() * 6;\n  }\n}\n\nfunction loadTerrain() {\n\n  terrain = new Array(cols);\n  max = 0;\n  for (i = 0; i < cols; ++i) {\n    terrain[i] = new Array(rows);\n    for (j = 0; j < rows; ++j) {\n      terrain[i][j] = new Object();\n      d1 = d2 = 0;\n      for (k = 0; k < points; ++k) {\n        d1 += Math.sqrt((i - controls[k].x) * (i - controls[k].x) + \n        								(j - controls[k].y) * (j - controls[k].y));\n      }\n      c_ = 1;\n      for (k = 0; k < points; ++k) {\n        d2 = Math.sqrt((i - controls[k].x) * (i - controls[k].x) + \n        							 (j - controls[k].y) * (j - controls[k].y));\n        c_ *= 1 - (1 - (d2 / d1));\n      }\n      if (c_ > max) max = c_;\n      terrain[i][j].color = c_;\n    }\n  }\n  for (i = 0; i < cols; ++i) {\n    for (j = 0; j < rows; ++j) {\n      terrain[i][j].color /= max;\n    }\n  }\n}\n\nfunction interpolate(x, y, degree) {\n\n  return degree * x + (1 - degree) * y;\n}\n\nfunction doLogic() {\n\n  frames++;\n  t = .5 - Math.cos(frames / 100) / 2;\n  for (i = 0; i < controls.length; ++i) {\n    if (controls[i].x + controls[i].vx >= cols || \n    		controls[i].x + controls[i].vx < 0) controls[i].vx *= -1;\n    if (controls[i].y + controls[i].vy >= rows ||\n    		controls[i].y + controls[i].vy < 0) controls[i].vy *= -1;\n    p = pi * 2 / controls.length * i + frames / 10 + \n    		(i % 2 ? 1 : -1) * Math.sin(frames / 16) * 2;\n    ls = rows / 2 * (1 + (i % 2));\n    x_ = cols / 2 + Math.sin(p) * ls;\n    y_ = rows / 2 + Math.cos(p) * ls;\n    vx = (x_ - controls[i].x) / 25;\n    vy = (y_ - controls[i].y) / 25;\n    controls[i].x += interpolate(controls[i].vx, vx, t);\n    controls[i].y += interpolate(controls[i].vy, vy, t);\n  }\n  loadTerrain();\n}\n\nfunction colorString(col){\n  var r = Math.round(col[0]*255);\n  var g = Math.round(col[1]*255);\n  var b = Math.round(col[2]*255);\n  return \"#\"+(\"0\" + r.toString(16) ).slice (-2)+(\"0\" + g.toString(16) ).slice (-2)+(\"0\" + b.toString(16) ).slice (-2);\n}\n\nfunction interpolateColors(RGB1, RGB2, degree) {\n\n  w1 = degree;\n  w2 = 1 - w1;\n  return colorString([w1 * RGB1[0] + w2 * RGB2[0], \n  										w1 * RGB1[1] + w2 * RGB2[1], \n                      w1 * RGB1[2] + w2 * RGB2[2]]);\n}\n\nrecordingStarted=playing=vidplaying=analyzerSetup=0\n\nfftSize = 64\n\nc=document.querySelector(\'#c\')\nx=c.getContext(\'2d\')\nc.width=c.clientWidth\nc.height=c.clientHeight\ncanvas2=c.cloneNode()\nx2=canvas2.getContext(\'2d\')\nS=Math.sin\nC=Math.cos\nt=0\n\n\nDraw=()=>{\n\n  if(!t){\n    initVars()\n    loadControlPoints()\n    loadTerrain()\n  }\n\n  x.clearRect(0, 0, cx * 2, cy * 2);\n\n  width = cx * 2 / cols;\n  height = cy * 2 / rows;\n  for (i = 0; i < cols; ++i) {\n    for (j = 0; j < rows; ++j) {\n      x_ = i * width;\n      y_ = j * height;\n      x.globalAlpha = 1;\n      col1 = [terrain[i][j].color,\n              terrain[i][j].color,\n              terrain[i][j].color];\n      p = terrain[i][j].color * pi * \n          (5.65 - Math.cos(frames / 22) * 5) + frames / 14;\n      col2 = [.5 + Math.sin(p) / 2,\n              .5 + Math.cos(p) / 2,\n              .5 - Math.sin(p) / 2];\n      x.fillStyle = interpolateColors(col2, col1, \n                      .5 - Math.cos(frames / 42) / 2);\n      x.fillRect(x_, y_, width + 1, height + 1);\n    }\n  }\n\n  doLogic();\n  t+=1/60\n  requestAnimationFrame(Draw)\n}\n\nsetupAnalyzerAndContent = () => {\n  analyzerSetup=1\n  x.lineCap=x.lineJoin=\'round\'\n  loaded=0\n  mp3 = new Audio()\n  mp3.crossOrigin = \"anonymous\"\n  mp3.src = song\n  if(!mp3)loaded=1\n  mp3.addEventListener(\'canplay\',()=>{\n    if(!playing){\n      loaded=playing=1\n      audioCtx = new (window.AudioContext || window.webkitAudioContext)()\n      analyser = audioCtx.createAnalyser()\n      source = audioCtx.createMediaElementSource(mp3)\n      source.connect(analyser)\n      analyser.connect(audioCtx.destination)\n      analyser.fftSize=fftSize\n      trim=analyser.fftSize*.25\n      bufferLength = analyser.frequencyBinCount\n      mp3.loop=true      \n      mp3.play()\n      Draw()\n      if(vid){\n        bgvid = document.createElement(\'video\')\n        bgvid.src = vid\n        bgvid.addEventListener(\'canplay\',()=>{\n          if(!vidplaying){\n            vidplaying=1\n            bgvid.loop=true\n            bgvid.muted=true\n            bgvid.play()\n          }\n        })\n      }\n    }\n  })\n}\n\nsetupAnalyzerAndContent()', 'body,html{\n  height: 100vh;\n  margin: 0;\n  background: linear-gradient(45deg, #000, #123)\n}', '<canvas id=c>', 'when the moon was made', 1, 'cantelope', '', 879, 'https://code.whitehotrobot.com/thumbs/b97ec01d1cf808ff051b4df80d4a0803.jpg', 217, 0),
