yser.fftSize=fftSize\n      trim=analyser.fftSize*.25\n      bufferLength = analyser.frequencyBinCount\n      mp3.loop=true      \n      mp3.play()\n      Draw()\n      if(vid){\n        bgvid = document.createElement(\'video\')\n        bgvid.src = vid\n        bgvid.addEventListener(\'canplay\',()=>{\n          if(!vidplaying){\n            vidplaying=1\n            bgvid.loop=true\n            bgvid.muted=true\n            bgvid.play()\n          }\n        })\n      }\n    }\n  })\n}\n\nsetupAnalyzerAndContent()', 'body,html{\n  height: 100vh;\n  margin: 0;\n  background: linear-gradient(45deg, #000, #123)\n}', '<canvas id=c>', 'symbols from beyond', 1, 'cantelope', '[600063]', 853, 'https://code.whitehotrobot.com/thumbs/95c5cdb93be3528391d1f8e75e47455a.jpg', 185, 0);
