(600068, 'https://jsbot.cantelope.org/uploads/13U73y.mp4', 'song = \'https://cantelope.org/sierpenski.mp3\'\nvid = \'\'\nrecordingStarted=playing=vidplaying=analyzerSetup=0\n\nfftSize = 64\n\nc=document.querySelector(\'#c\')\nx=c.getContext(\'2d\')\nc.width=c.clientWidth\nc.height=c.clientHeight\ncanvas2=c.cloneNode()\nx2=canvas2.getContext(\'2d\')\nS=Math.sin\nC=Math.cos\nt=0\n\nDraw=()=>{\n   x.fillStyle=\'#0008\',x.fillRect(0,0,w=c.width,w)\n  \n  if(!t){\n    Q=q=>[c.width/2+X/Z*900,c.height/2+Y/Z*900]\n    I=(A,B,M,D,E,F,G,H)=>(K=((G-E)*(B-F)-(H-F)*(A-E))/(J=(H-F)*(M-A)-(G-E)*(D-B)))>=0&&K<=1&&(L=((M-A)*(B-F)-(D-B)*(A-E))/J)>=0&&L<=1?[A+K*(M-A),B+K*(D-B)]:0\n    loopCount=0\n    transitionFreq=16\n  }\n  \n  switch(loopCount%4){\n    case 1:\n      Rl=-Math.PI/2+S(t/2.5)/2,Pt=-.2+S(t/2)/2,Yw=-t/2.5\n      break\n    case 2:\n      Rl=-Math.PI/2+S(t/2.5)/2,Pt=-.3-S(t/2)/3,Yw=t/2.5\n      break\n    case 3:\n      Rl=-Math.PI/2+S(t/2.5)/2,Pt=-.2-S(t*1.5)/4,Yw=-t/2.5\n      break\n    case 0:\n      Rl=-Math.PI/2+S(t/2.5)/2,Pt=-.3-S(t/2)/3,Yw=t/2.5\n      break\n  }        \n\n  init = () => {\n    t=1/60\n    switch(loopCount%4){\n      case 0:\n        oX=0,oY=-2,oZ=14\n        lw=16e6\n        cl=3\n        grav=.01\n        floor=6\n        ls=.7\n        iSr=4\n        iBv=.1\n        lsrd=1.85\n        tiSrd=2\n        tOx=-5.25\n        depth=4\n        curveres=4\n        break\n      case 1:\n        oX=0,oY=-2.5,oZ=15\n        lw=16e6\n        cl=4\n        grav=.01\n        floor=5.5\n        ls=.7\n        iSr=5\n        iBv=.1\n        lsrd=1.85\n        tiSrd=2\n        tOx=-5\n        depth=3\n        curveres=5\n        break\n      case 2:\n        oX=0,oY=-2.25,oZ=14\n        lw=16e6\n        cl=5\n        grav=.01\n        floor=6\n        ls=.75\n        iSr=5\n        iBv=.06\n        lsrd=1.95\n        tiSrd=2.5\n        tOx=-5\n        depth=2\n        curveres=5\n        break\n      case 3:\n        oX=0,oY=-2.25,oZ=14\n        lw=16e6\n  