ty\n        Z+=tz\n        R(Rl,Pt,Yw,1)\n        x.fillStyle=\'#0ffa\'\n        s=2000/Z\n        l=Q()\n        x.fillRect(l[0]-s/2,l[1]-s/2,s,s)\n        return q\n      })\n      return v\n    })\n\n\n    x.strokeStyle=\'#affc\'\n    B.map((v,m)=>{\n      tx=v[0]\n      ty=v[1]\n      tz=v[2]\n      v[3].map((q,i)=>{\n        q[4]=q[4].map((v,j)=>{\n            B.map((q,j)=>{\n              if(m!=j){\n                d=(1+Math.hypot(q[0]-v[0],q[1]-v[1],q[2]-v[2]))**2.5\n                v[3]+=(q[0]-v[0])*100/d\n                v[4]+=(q[1]-v[1])*100/d\n                v[5]+=(q[2]-v[2])*100/d\n              }\n            })\n            X=v[0]\n            Y=v[1]\n            Z=v[2]\n            R(Rl,Pt,Yw,1)\n            s=(1+200/Z*(v[6]-.3))**2\n            v[6]/=1.05\n          if(j){\n            x.beginPath()\n            if(Z>0)x.lineTo(...Q())\n            X=q[4][j-1][0]\n            Y=q[4][j-1][1]\n            Z=q[4][j-1][2]\n            R(Rl,Pt,Yw,1)\n            if(Z>0)x.lineTo(...Q())\n            x.strokeStyle=`hsla(${360/2/q[4].length*j-t*100},99%,${50+50/q[4].length*j**2/16}%,.05)`\n            x.lineWidth=Math.min(20, 1800/Z*s)\n            x.stroke()\n          }\n          v[0]+=v[3]\n          v[1]+=v[4]\n          v[2]+=v[5]\n          return v\n        }).filter(v=>{\n          return v[6]>.3\n        })\n        x.beginPath()\n        v[3].map((n,j)=>{\n          if(j>i){\n            d=Math.hypot(q[0]-n[0],q[1]-n[1],q[2]-n[2])\n            if(d<q[3]*1.25){\n              X=tx+q[0]\n              Y=ty+q[1]\n              Z=tz+q[2]\n              R(Rl,Pt,Yw,1)\n              if(Z>0)x.moveTo(...Q())\n              X=tx+n[0]\n              Y=ty+n[1]\n              Z=tz+n[2]\n              R(Rl,Pt,Yw,1)\n              if(Z>0)x.lineTo(...Q())\n            }\n          }\n        })\n        X=tx,Y=ty,Z=tz\n        R(Rl,Pt,Yw,1)\n        x.strokeStyle=\'#0f42\'\n        x.lineWidth=3000/Z\n        x.stroke()\n        x.strokeStyle=\'#fff8\'\n        x.lineWidth=1+200/Z\n        x.stroke()\n      })\n    })\n\n  t+=1/60\n  requestAnimationFrame(Draw)\n}\n\nsetupAnalyzerAndContent = () => {\n  analyzerSetup=1\n  loaded=0\n  mp3 = new Audio()\n  mp3.crossOrigin = \"anonymous\"\n  mp3.src = song\n  mp3.loop = true\n  if(!mp3)loaded=1\n  mp3.addEventListener(\'canplay\',()=>{\n    if(!playing){\n      loaded=playing=1\n      audioCtx = new (window.AudioContext || window.webkitAudioContext)()\n      analyser = audioCtx.createAnalyser()\n      source = audioCtx.createMediaElementSource(mp3)\n      source.connect(analyser)\n      analyser.connect(audioCtx.destination)\n      analyser.fftSize=fftSize\n      trim=analyser.fftSize*.25\n      bufferLength = analyser.frequencyBinCount\n      mp3.loop=true      \n      mp3.play()\n      Draw()\n      if(vid){\n        bgvid = document.createElement(\'video\')\n        bgvid.src = vid\n        bgvid.addEventListener(\'canplay\',()=>{\n          if(!vidplaying){\n            vidplaying=1\n            bgvid.loop=true\n            bgvid.muted=true\n            bgvid.play()\n          }\n        })\n      }\n    }\n  })\n}\n\nsetupAnalyzerAndContent()', 'body,html{\n  height: 100vh;\n  margin: 0;\n  background: linear-gradient(45deg, #000, #123)\n}', '<canvas id=c>', 'geoflux', 1, 'cantelope', '[600110,600109,600108,600104,600103,600102,600101,600100,600099,600092,600091,600090,600089,600088,600086,600085,600084,600080,600079,600078,600075,600071,600070,600069,600068,600066,600063]', 331, 'https://code.whitehotrobot.com/thumbs/0d2bce6ffa2e3b0ce3fd689ef1132f22.jpg', 8, 0),
