\n    Z=((i/rw/cl|0)-br/2+.5)*(l=(4+S(t))*4)-((t*10)%1)*l\n    return [X, Y, Z]\n  })\n\n  if(go){\n    delay = 5\n    if(bgvid1.ended && bgvid2.currentTime >= bgvid2.duration-bgvid2.duration/delay){\n      bgvid1.play()\n    }\n    if((bgvid2.paused || bgvid2.ended) && bgvid1.currentTime >= bgvid1.duration-bgvid1.duration/delay){\n      bgvid2.play()\n    }\n    x.globalCompositeOperation = \'source-over\'\n    c.width|=0\n    x.lineCap=x.lineJoin=\'butt\'\n    x.fillStyle=`hsla(${t*200},99%,50%,1)`, x.fillRect(0,0,c.width,c.height)\n    alpha1 = Math.min(1, bgvid1.currentTime/bgvid1.duration*delay * Math.min(1, (bgvid1.duration-bgvid1.currentTime)/bgvid1.duration))\n    if(alpha1>.01){\n      x.globalAlpha=alpha1\n      x.drawImage(bgvid1, 0, 0, c.width, c.height)\n    }\n    alpha2 = Math.min(1, bgvid2.currentTime/bgvid2.duration*delay * Math.min(1, (bgvid2.duration-bgvid2.currentTime)/bgvid2.duration))\n    if(alpha2>.01){\n      x.globalAlpha=alpha2\n      x.drawImage(bgvid2, 0, 0, c.width, c.height)\n    }\n    \n    if(bgvid3.ended && bgvid4.currentTime >= bgvid4.duration-bgvid4.duration/delay){\n      bgvid3.play()\n    }\n    if((bgvid4.paused || bgvid4.ended) && bgvid3.currentTime >= bgvid3.duration-bgvid3.duration/delay){\n      bgvid4.play()\n    }\n    x.globalCompositeOperation = \'difference\'\n    alpha3 = Math.min(1, bgvid3.currentTime/bgvid3.duration*delay * Math.min(1, (bgvid3.duration-bgvid3.currentTime)/bgvid3.duration))\n    if(alpha3>.01){\n      x.globalAlpha=alpha3\n      x.drawImage(bgvid3, 0, 0, c.width, c.height)\n    }\n    alpha4 = Math.min(1, bgvid4.currentTime/bgvid4.duration*delay * Math.min(1, (bgvid4.duration-bgvid4.currentTime)/bgvid4.duration))\n    if(alpha4>.01){\n      x.globalAlpha=alpha4\n      x.drawImage(bgvid4, 0, 0, c.width, c.height)\n    }\n  } else {\n    x.fillStyle=\'#0008\',x.fillRect(0,0,c.width,c.height)\n  }\n\n  x.globalCompositeOperation=\'source-over\'\n  x.globalAlpha = 1\n\n  oX=0, oY=0, oZ=20\n  Rl=C(t)*3, Pt=S(t/4)/4, Yw=t/2\n  B.map(v=>{\n    tx=v[0]\n    ty=v[1]\n    tz=v[2]\n    CB.map((q, j)=>{\n      if(!(j%4)) x.beginPath()\n      X = tx + q[0]\n      Y = ty + q[1]\n      Z = tz + q[2]\n      R(Rl,Pt,Yw,1)\n      if(Z>0)x.lineTo(...Q())\n      if(j%4==3){\n        x.closePath()\n        x.lineWidth = Math.min(40, 1+1600/Z/Z)\n        x.strokeStyle=`hsla(${170+t*200},99%,85%,${Math.max(0,.2-Math.abs(tz**2)/19999)})`\n        x.stroke()\n        x.fillStyle=`hsla(${170+t*200},99%,40%,${Math.max(0,.2-Math.abs(tz**2)/19999)})`\n        x.fill()\n      }\n    })\n  })\n  t+=1/60\n  requestAnimationFrame(Draw)\n}\n\nsetupAnalyzerAndContent = () => {\n  analyzerSetup=1\n  loaded=0\n  mp3 = new Audio()\n  mp3.crossOrigin = \"anonymous\"\n  mp3.src = song\n  mp3.loop = true\n  if(!mp3)loaded=1\n  mp3.addEventListener(\'canplay\',()=>{\n    if(!playing){\n      loaded=playing=1\n      audioCtx = new (window.AudioContext || window.webkitAudioContext)()\n      analyser = audioCtx.createAnalyser()\n      source = audioCtx.createMediaElementSource(mp3)\n      source.connect(analyser)\n      analyser.connect(audioCtx.destination)\n      analyser.fftSize=fftSize\n      trim=analyser.fftSize*.25\n      bufferLength = analyser.frequencyBinCount\n      mp3.loop=true      \n      Draw()\n      if(vid){\n        bgvid = document.createElement(\'video\')\n        bgvid.src = vid\n        bgvid.addEventListener(\'canplay\',()=>{\n          if(!vidplaying){\n            vidplaying=1\n            bgvid.loop=true\n            bgvid.muted=true\n            bgvid.play()\n          }\n        })\n      }\n    }\n  })\n}\n\nsetupAnalyzerAndContent()', 'body,html{\n  height: 100vh;\n  margin: 0;\n  background: linear-gradient(45deg, #000, #123)\n}\ncanvas{\n  width:100%;\n  height: 100%;\n}', '<canvas id=c>', 'miami nights', 1, 'cantelope', '[600120,600119,600117,600116,600115,600114,600113,600112,600111,600110,600109,600108,600104,600103,600102,600101,600100,600099,600092,600091,600090,600