arse(JSON.stringify(B)).map(v=>{\n          v[1]-=6\n          v[0]+=10\n          return v\n        }),\n      ]\n      P=Array(1).fill().map((v,j)=>{\n        return [-50,S(p=Math.PI*2/3*j)*.35,C(p)*.35,2,(Rn()-.5)/20,(Rn()-.5)/20]\n      })\n    }\n  }\n\n  if(!((t*60|0)%120)){\n    init()\n    Rl=0,Pt=t/2,Yw=Math.PI/6+Math.PI/1.5*Rn()\n    Rl=-.25+(Rn()-.5)*.5\n  }\n\n  if(go){\n    x.globalAlpha=.25\n    x.drawImage(bgimg,c.width/2-(s=bgimg.width*(c.height/bgimg.height))/2,0,s,c.height)\n    x.globalAlpha=1\n    x.fillStyle=\'#0003\',x.fillRect(0,0,c.width,c.height)\n  }else{\n    x.fillStyle=\'#000a\',x.fillRect(0,0,c.width,c.height)\n  }\n  iPr=2, iBr=.5\n  Pt-=.02\n  oX=0,oY=0,oZ=28+S(t*2)*12\n  \n  DC=(tx,ty,tz,r,rl,pt,yw)=>{\n    cx=X,cy=Y,cz=Z\n    for(m=18;m--;){\n      x.beginPath()\n      for(let i=8;i--;){\n        X=S(p=Math.PI*2/8*i)*r\n        Y=C(p)*r\n        Z=0\n        if(m<6){\n          R(0,0,Math.PI*2/12*m,0)\n        }else if(m<12){\n          R(0,Math.PI*2/12*m,0,0)\n        }else{\n          R(0,Math.PI*2/12*m,Math.PI/2,0)\n        }\n        R(rl,pt,yw,0)\n        X+=tx\n        Y+=ty\n        Z+=tz\n        R(Rl,Pt,Yw,1)\n        if(Z>.1)x.lineTo(...Q())\n      }\n      x.closePath()\n      x.lineWidth=1+Math.min(60, 100/Z/Z)\n      x.stroke()\n    }\n    X=cx,Y=cy,Z=cz\n  }\n\n\n  P.map(v=>{\n    X=v[0]+=v[3]\n    Y=v[1]+=v[4]\n    Z=v[2]+=v[5]\n    //R(Rl,Pt,Yw,1)\n    x.strokeStyle=\'#2cd8\'\n    DC(X,Y,Z,iPr,0,0,0)\n  })\n  B.map((v,i)=>{\n    P.map(q=>{\n      d=Math.hypot(mx=v[0]-q[0],my=v[1]-q[1],mz=v[2]-q[2])\n      if(d<iBr+iPr){\n        q[3]+=v[3]/4/iPr\n        q[4]+=v[4]/4/iPr\n        q[5]+=v[5]/4/iPr\n        q[3]-=mx/4/iPr\n        q[4]-=my/4/iPr\n        q[5]-=mz/4/iPr\n        v[3]+=q[3]/8/iBr\n        v[4]+=q[4]/8/iBr\n        v[5]+=q[5]/8/iBr\n        v[3]+=mx/8/iBr\n        v[4]+=my/8/iBr\n        v[5]+=mz/8/iBr\n      }\n    })\n    B.map((q,j)=>{\n      if(i!=j){\n        d=Math.hypot(mx=v[0]-q[0],my=v[1]-q[1],mz=v[2]-q[2])\n        if(d<iBr*1.95){\n          q[3]+=v[3]/6/iBr\n          q[4]+=v[4]/6/iBr\n          q[5]+=v[5]/6/iBr\n          q[3]-=mx/8/iBr\n          q[4]-=my/8/iBr\n          q[5]-=mz/8/iBr\n          v[3]+=mx/8/iBr\n          v[4]+=my/8/iBr\n          v[5]+=mz/8/iBr\n        }\n      }\n    })\n    X=v[0]+=v[3]\n    Y=v[1]+=v[4]\n    Z=v[2]+=v[5]\n    x.strokeStyle=\'#1f83\'\n    DC(X,Y,Z,iBr,0,0,0)\n  })\n  t+=1/60\n  requestAnimationFrame(Draw)\n}\n\nsetupAnalyzerAndContent = () => {\n  analyzerSetup=1\n  loaded=0\n  mp3 = new Audio()\n  mp3.crossOrigin = \"anonymous\"\n  mp3.src = song\n  mp3.loop = true\n  if(!mp3)loaded=1\n  mp3.addEventListener(\'canplay\',()=>{\n    if(!playing){\n      loaded=playing=1\n      audioCtx = new (window.AudioContext || window.webkitAudioContext)()\n      analyser = audioCtx.createAnalyser()\n      source = audioCtx.createMediaElementSource(mp3)\n      source.connect(analyser)\n      analyser.connect(audioCtx.destination)\n      analyser.fftSize=fftSize\n      trim=analyser.fftSize*.25\n      bufferLength = analyser.frequencyBinCount\n      mp3.loop=true\n      mp3.play()\n      Draw()\n      if(vid){\n        bgvid = document.createElement(\'video\')\n        bgvid.src = vid\n        bgvid.addEventListener(\'canplay\',()=>{\n          if(!vidplaying){\n            vidplaying=1\n            bgvid.loop=true\n            bgvid.muted=true\n            bgvid.play()\n          }\n        })\n      }\n    }\n  })\n}\n\nsetupAnalyzerAndContent()', 'body,html{\n  height: 100vh;\n  margin: 0;\n  background: linear-gradient(45deg, #000, #123)\n}\ncanvas{\n  width:100%;\n  height: 100%;\n}', '<canvas id=c>', 'breaking formation', 1, 'cantelope', '[600121,600120,600119,600117,600116,600115,600114,600113,600112,600111,600110,600109,600108,600104,600103,600102,600101,600100,600099,600092,600091,600090,600089,600088,600086,600085,600084,600080,600079,600078,600075,600071,600070,600069,600068,600066,600063]', 165, 'https://code.whitehot