     v[0]=q[0]-vx*iBs*2\n            v[1]=q[1]-vy*iBs*2\n            v[2]=q[2]-vz*iBs*2\n            v[3]-=vx/5\n            v[4]-=vy/5\n            v[5]-=vz/5\n            q[0]=v[0]+vx*iBs*2\n            q[1]=v[1]+vy*iBs*2\n            q[2]=v[2]+vz*iBs*2\n            q[3]+=vx/5\n            q[4]+=vy/5\n            q[5]+=vz/5\n          }\n        }\n      })\n      d=Math.hypot(v[3],v[4],v[5])\n      v[3]=v[3]/d*iBv\n      v[4]=v[4]/d*iBv\n      v[5]=v[5]/d*iBv\n      v[0]+=v[3]\n      v[1]+=v[4]\n      v[2]+=v[5]\n      if(v[0]>iGs/2-iBs)v[0]=iGs/2-iBs,v[3]*=-1\n      if(v[0]<-iGs/2+iBs)v[0]=-iGs/2+iBs,v[3]*=-1\n      if(v[1]>iGs/2-iBs)v[1]=iGs/2-iBs,v[4]*=-1\n      if(v[1]<-iGs/2+iBs)v[1]=-iGs/2+iBs,v[4]*=-1\n      if(v[2]>iGs/2-iBs)v[2]=iGs/2-iBs,v[5]*=-1\n      if(v[2]<-iGs/2+iBs)v[2]=-iGs/2+iBs,v[5]*=-1\n      x.beginPath()\n      X=v[0],Y=v[1],Z=v[2]\n      R()\n      x.arc(...Q(),iBs*1e3/Z,0,7)\n      x.fillStyle=\'#0ff3\'\n      x.fill()\n    })\n\n    sd=2\n\n    Sk=Sk.map(v=>{\n      d=1/12\n      lx1=v[0]\n      ly1=v[1]\n      lz1=v[2]\n      lx2=v[0]+(v[3]-v[0])*d\n      ly2=v[1]+(v[4]-v[1])*d\n      lz2=v[2]+(v[5]-v[2])*d\n      v=[lx1,ly1,lz1,lx2,ly2,lz2,Math.hypot(lx1-lx2,ly1-ly2,lz1-lz2),v[7],v[8],v[9]]\n      return v\n    })\n    \n    \n    for(j=16;j--;){\n      tSk=[]\n      for(m=2;m--;){\n        Sk=Sk.map(v=>{\n          d=v[6]\n          lx1=v[3]\n          ly1=v[4]\n          lz1=v[5]\n          n=(1/(1+(L=Math.hypot(v[7]-v[3],v[8]-v[4],v[9]-v[5]))))\n          a=((v[0]-v[3])+(v[3]-v[7])*n)/2\n          b=((v[2]-v[5])+(v[5]-v[9])*n)/2\n          e=((v[1]-v[4])+(v[4]-v[8])*n)/2\n          U=Math.min(1.5,L**3/100)\n          p1=Math.atan2(a,b)+(Rn()-.5)*U\n          p2=Math.acos(e/Math.hypot(a,b,e))+(Rn()-.5)*U\n          lx2=v[3]-S(p1)*S(p2)*d\n          ly2=v[4]-C(p2)*d\n          lz2=v[5]-C(p1)*S(p2)*d\n          if(Rn()<.12)tSk=[...tSk,[lx1,ly1,lz1,lx2,ly2,lz2,v[6],v[7],v[8],v[9]]]\n          return v\n        })\n      }\n      Sk=[...