(600004, 'var c = document.querySelector(\'#c\')\nc.width = 1e3\nc.height = 1e3\nvar x = c.getContext(\'2d\')\nvar S = Math.sin\nvar C = Math.cos\nvar t=0, oX=0, oY=0, oZ=12\n\nvar R=(Rl,Pt,Yw,o)=>{\n  X=S(p=(A=(M=Math).atan2)(X,Y)+Rl)*(d=(H=M.hypot)(X,Y))\n  Y=C(p)*d\n  Y=S(p=A(Y,Z)+Pt)*(d=H(Y,Z))\n  Z=C(p)*d\n  X=S(p=A(X,Z)+Yw)*(d=H(X,Z))\n  Z=C(p)*d\n  if(o)X+=oX,Y+=oY,Z+=oZ\n}\n\nvar Q=()=>[c.width/2+X/Z*600,c.height/2+Y/Z*600]\n\n\nDraw = () => {\n\n  x.globalAlpha=.5\n  x.clearRect(0,0,w=c.width,w)\n\n  Rl=-t*1.5\n  Pt=0\n  Yw=S(t/2)*4\n\n  let P=Array(6).fill().map((v,i)=>{\n    X=S(p=Math.PI*2/6*i+Math.PI/6)\n    Y=C(p)\n    Z=0\n    return [X,Y,Z]\n  })\n  let ls=1.6\n  let B=Array(6).fill().map((v,i)=>{\n    let q=Math.PI*2/6*i-t*8\n    s=ls*(1+(1+S(q)/2)*(1+S(t*1.5))/3)\n    X=S(p=Math.PI*2/6*i)*s\n    Y=C(p)*s\n    Z=0\n    return [X,Y,Z,[...P]]\n  })\n  B=[...B,[0,0,0,[...P]]]\n\n  x.lineJoin=x.lineCap=\'round\'\n\n  B.map(v=>{\n    tx=v[0]\n    ty=v[1]\n    tz=v[2]\n    x.beginPath()\n    v[3].map(q=>{\n      X=tx+q[0]\n      Y=ty+q[1]\n      Z=tz+q[2]\n      R(Rl,Pt,Yw,1)\n      x.lineTo(...Q())\n    })\n\n    x.closePath()\n    x.lineWidth=140/Z\n    x.strokeStyle=\'#4bc8\'\n    x.fillStyle=\'#2cf8\'\n    x.fill()\n    x.stroke()\n  })\n\n  this.t += 1/60\n  requestAnimationFrame(Draw)\n}\n\nDraw()', 'html,body{\n  background: linear-gradient(-45deg, #024, #000);\n  margin: 0;\n  font-family: arial, tahoma;\n  font-size: 24px;\n  color: #ff0;\n  min-height: 100%;\n  overflow: hidden;\n}\n#c{\n  width: 800px;\n  height: 800px;\n}\n.demoPageText{\n  text-align: center;\n  position: absolute;\n  top: 50%;\n  transform: translatey(-50%);\n  text-shadow: 2px 2px 2px #000;\n}\n.mainInner{\n  display: flex;\n  flex-wrap: wrap;\n  justify-content: space-evenly;\n}\n.main{\n  position: absolute;\n  width: 700px;\n  left: 50%;\n  top: 50%;\n  transform: translate(-50%, -50%);\n}', '<div class=\"main\">\n  <div class=\"mainInner\">\n    <div class=\"demoPageTex